// Function to safely escape JSON data and remove „Äê„Äë encapsulated content
function processText(data) {
  if (typeof data === 'string') {
    // First remove content between „Äê„Äë
    let processed = data.replace(/„Äê[^„Äë]*„Äë/g, '');

    // Then escape special characters
    return processed
      .replace(/\\/g, '\\\\') // Escape backslashes
      .replace(/"/g, '\\"')    // Escape double quotes
      .replace(/\n/g, '\\n')   // Escape newlines
      .replace(/\r/g, '\\r')   // Escape carriage returns
      .replace(/\t/g, '\\t');  // Escape tabs
  } else if (typeof data === 'object' && data !== null) {
    // Recursively process object properties
    for (const key in data) {
      if (data.hasOwnProperty(key)) {
        data[key] = processText(data[key]);
      }
    }
  }
  return data;
}

