// Remove \n (real), \\n (escapado) e \r\n apenas nas bordas
function stripEdgeNewlines(s) {
  if (typeof s !== 'string') return s;
  s = s.replace(/^((\r?\n)|\\n)+/, '');   // in√≠cio
  s = s.replace(/((\r?\n)|\\n)+$/, '');   // fim
  return s;
}

// Aplica recursivamente em todo o JSON do item
function sanitize(obj) {
  if (Array.isArray(obj)) return obj.map(sanitize);
  if (obj && typeof obj === 'object') {
    for (const k of Object.keys(obj)) obj[k] = sanitize(obj[k]);
    return obj;
  }
  return stripEdgeNewlines(obj);
}

