// Obtem o texto da entrada
const text = $json["Resumo"];
const maxLength = 2000;

function splitLongString(text, maxLength) {
  const result = [];
  let currentIndex = 0;

  while (currentIndex < text.length) {
    let chunk = text.slice(currentIndex, currentIndex + maxLength);

    // Evita cortar no meio de palavras
    if (chunk.length === maxLength) {
      const lastSpace = chunk.lastIndexOf(' ');
      if (lastSpace > -1 && currentIndex + maxLength < text.length) {
        chunk = chunk.slice(0, lastSpace);
        currentIndex += lastSpace + 1;
      } else {
        currentIndex += maxLength;
      }
    } else {
      currentIndex += maxLength;
    }

    result.push(chunk);
  }

  return result;
}

const splitted = splitLongString(text, maxLength);

// Retorna cada pedaço com índice começando em 1
return splitted.map((part, index) => ({
  json: {
    trecho: part,
    count: index + 1
  }
}));

