// ---- helpers ----
function toEvolutionJid(raw) {
  if (!raw) return null;
  let s = String(raw).trim();

  if (/@(g\.us|c\.us|s\.whatsapp\.net)$/i.test(s)) {
    return s
      .replace(/@c\.us$/i, '@s.whatsapp.net')
      .replace(/@S\.WHATSAPP\.NET$/i, '@s.whatsapp.net');
  }
  if (/-group$/i.test(s)) return s.replace(/-group$/i, '@g.us');
  if (/^\d{6,}-\d{3,}$/.test(s)) return s + '@g.us';
  if (/^120\d{14,}$/.test(s)) return s + '@g.us';

  const digits = s.replace(/\D+/g, '');
  if (digits.length >= 6) return digits + '@s.whatsapp.net';

  return raw;
}

